public class ZoneManager\n{\n    [DllImport("urlmon.dll", ExactSpelling = true)]\n    public static extern int CoInternetCreateZoneManager(IntPtr pIServiceProvider, out IInternetZoneManager ppIInternetZoneManager, uint dwReserved);\n\n    public static void Main()\n    {\n        IntPtr servicePtr = IntPtr.Zero;\n        IInternetZoneManager zoneManager;\n        int hr = CoInternetCreateZoneManager(servicePtr, out zoneManager, 0);\n        if (hr != 0)\n        {\n            throw new COMException("Failed to create IInternetZoneManager", hr);\n        }\n\n        ZONEATTRIBUTES zoneAttributes = new ZONEATTRIBUTES\n        {\n            cbSize = (uint)Marshal.SizeOf(typeof(ZONEATTRIBUTES))\n        };\n\n        hr = zoneManager.GetZoneAttributes(3, ref zoneAttributes); // Example for Internet Zone (3)\n        if (hr != 0)\n        {\n            throw new COMException("Failed to get zone attributes", hr);\n        }\n\n        Console.WriteLine($"Zone Display Name: {zoneAttributes.szDisplayName}");\n        Console.WriteLine($"Zone Description: {zoneAttributes.szDescription}");\n    }\n}