private void CustomizeDataGridView()\n{\n    // Setting the background color and grid color\n    dataGridView.BackgroundColor = Color.White;\n    dataGridView.GridColor = Color.LightGray;\n\n    // Setting the column headers style\n    DataGridViewCellStyle columnHeaderStyle = new DataGridViewCellStyle();\n    columnHeaderStyle.BackColor = Color.Navy;\n    columnHeaderStyle.ForeColor = Color.White;\n    columnHeaderStyle.Font = new Font("Verdana", 10, FontStyle.Bold);\n    dataGridView.ColumnHeadersDefaultCellStyle = columnHeaderStyle;\n\n    // Setting the rows style\n    DataGridViewCellStyle rowStyle = new DataGridViewCellStyle();\n    rowStyle.BackColor = Color.LightBlue;\n    rowStyle.Font = new Font("Verdana", 10);\n    dataGridView.RowsDefaultCellStyle = rowStyle;\n\n    // Alternating row style\n    DataGridViewCellStyle alternatingRowStyle = new DataGridViewCellStyle();\n    alternatingRowStyle.BackColor = Color.White;\n    dataGridView.AlternatingRowsDefaultCellStyle = alternatingRowStyle;\n\n    // Setting the selection style\n    dataGridView.SelectionMode = DataGridViewSelectionMode.FullRowSelect;\n    dataGridView.DefaultCellStyle.SelectionBackColor = Color.DarkBlue;\n    dataGridView.DefaultCellStyle.SelectionForeColor = Color.White;\n\n    // Additional settings\n    dataGridView.AutoSizeColumnsMode = DataGridViewAutoSizeColumnsMode.Fill;\n    dataGridView.EnableHeadersVisualStyles = false;\n    dataGridView.ColumnHeadersHeightSizeMode = DataGridViewColumnHeadersHeightSizeMode.AutoSize;\n}\n\n// Call this method in your form's constructor or Load event\npublic Form1()\n{\n    InitializeComponent();\n    CustomizeDataGridView();\n}