public class Message\n{\n    public virtual long Id { get; set; }\n    public virtual long From { get; set; }\n    public virtual long To { get; set; }\n    public virtual string Body { get; set; }\n    public virtual long? ParentId { get; set; } // Nullable\n    public virtual byte SenderType { get; set; }\n    public virtual bool IsRead { get; set; }\n\n    private IList<Message> answers = new List<Message>();\n    public virtual IList<Message> Answers\n    {\n        get { return answers; }\n        set { answers = value; }\n    }\n\n    public virtual Message Parent { get; set; }\n}