protected void Button2_Click(object sender, EventArgs e)\n{\n    int showno = 4; // Example count, replace with your actual logic\n    List<Showing> showings = cBLL.GetShowings(tenant.Id);\n\n    for (int i = 1; i <= showno; i++)\n    {\n        UserControl uc = (UserControl)Page.LoadControl("Controls/BookShowing.ascx");\n        uc.ID = "showing_" + i;\n        uc.EnableViewState = true;\n        pnl_showings.Controls.Add(uc);\n    }\n\n    // Save the count in ViewState\n    ViewState["ShowingsCount"] = showno;\n    UpdatePanel1.Update();\n}\n\nprotected override void OnInit(EventArgs e)\n{\n    base.OnInit(e);\n\n    // Recreate dynamic controls on postback\n    if (ViewState["ShowingsCount"] != null)\n    {\n        int noOfUCs = (int)ViewState["ShowingsCount"];\n        for (int i = 1; i <= noOfUCs; i++)\n        {\n            UserControl uc = (UserControl)Page.LoadControl("Controls/BookShowing.ascx");\n            uc.ID = "showing_" + i;\n            uc.EnableViewState = true;\n            pnl_showings.Controls.Add(uc);\n        }\n    }\n}\n\nprotected void btn_editShowings_Click(object sender, EventArgs e)\n{\n    // Example usage of finding the control\n    var control = FindControlRecursive(pnl_showings, "showing_1");\n    if (control != null)\n    {\n        control.Visible = false;\n    }\n}\n\nprivate Control FindControlRecursive(Control root, string id)\n{\n    if (root.ID == id) return root;\n    foreach (Control control in root.Controls)\n    {\n        Control foundControl = FindControlRecursive(control, id);\n        if (foundControl != null) return foundControl;\n    }\n    return null;\n}