using System;\nusing System.Collections.Generic;\n\n// Strategy Pattern for validation\npublic interface IValidationStrategy\n{\n    void Validate(Table table);\n}\n\npublic class CommonValidation : IValidationStrategy\n{\n    public void Validate(Table table)\n    {\n        // Common validation logic here\n        Console.WriteLine($"Common validation for {table.Name}");\n    }\n}\n\npublic class SpecificValidation : IValidationStrategy\n{\n    public void Validate(Table table)\n    {\n        // Specific validation logic here\n        Console.WriteLine($"Specific validation for {table.Name}");\n    }\n}\n\n// Composite Pattern for organizing validations\npublic class CompositeValidation : IValidationStrategy\n{\n    private readonly List<IValidationStrategy> _validations = new List<IValidationStrategy>();\n\n    public void Add(IValidationStrategy validation)\n    {\n        _validations.Add(validation);\n    }\n\n    public void Validate(Table table)\n    {\n        foreach (var validation in _validations)\n        {\n            validation.Validate(table);\n        }\n    }\n}\n\n// Table class\npublic class Table\n{\n    public string Name { get; set; }\n}\n\n// Example usage\npublic class Program\n{\n    public static void Main()\n    {\n        // Creating tables\n        var table1 = new Table { Name = "Table1" };\n        var table2 = new Table { Name = "Table2" };\n\n        // Creating validations\n        var commonValidation = new CommonValidation();\n        var specificValidation = new SpecificValidation();\n\n        // Composite validation for Table1\n        var compositeValidation1 = new CompositeValidation();\n        compositeValidation1.Add(commonValidation);\n        compositeValidation1.Add(specificValidation);\n\n        // Composite validation for Table2\n        var compositeValidation2 = new CompositeValidation();\n        compositeValidation2.Add(commonValidation);\n\n        // Validate tables\n        compositeValidation1.Validate(table1);\n        compositeValidation2.Validate(table2);\n    }\n}