using System;\nusing System.IO;\n\npublic partial class FileUploadDemo : System.Web.UI.Page\n{\n    protected void btnUpload_Click(object sender, EventArgs e)\n    {\n        // This event can be used for any additional processing after the file is uploaded\n        lblMessage.Text = "File uploaded successfully!";\n    }\n\n    protected void AsyncFileUpload1_UploadedComplete(object sender, AjaxControlToolkit.AsyncFileUploadEventArgs e)\n    {\n        if (AsyncFileUpload1.HasFile)\n        {\n            string savePath = Server.MapPath("~/Uploads/") + Path.GetFileName(AsyncFileUpload1.FileName);\n            AsyncFileUpload1.SaveAs(savePath);\n        }\n    }\n}