public void UpdatePosition(Position position)\n{\n    // Validate the GUID\n    if (position.PositionId == Guid.Empty)\n    {\n        throw new ArgumentException("Invalid PositionId");\n    }\n\n    // Fetch the record from the database\n    var dbPosition = dbContext.Positions.SingleOrDefault(p => p.PositionId == position.PositionId);\n    if (dbPosition == null)\n    {\n        throw new ArgumentException("Position not found");\n    }\n\n    // Perform authorization check\n    if (!UserHasPermissionToUpdate(dbPosition))\n    {\n        throw new UnauthorizedAccessException("User does not have permission to update this position");\n    }\n\n    // Update the record\n    dbPosition.PositionName = position.PositionName;\n    dbPosition.PositionType = position.PositionType;\n    dbContext.SaveChanges();\n}\n\nprivate bool UserHasPermissionToUpdate(Position dbPosition)\n{\n    // Implement your authorization logic here\n    return true; // Example: always true, replace with real logic\n}