using System;\nusing Excel = Microsoft.Office.Interop.Excel;\n\nclass Program\n{\n    static void Main()\n    {\n        Excel.Application excelApp = new Excel.Application();\n        \n        if (excelApp == null)\n        {\n            Console.WriteLine("Excel is not properly installed!!");\n            return;\n        }\n\n        excelApp.Visible = true;\n\n        // If you want to open an existing workbook\n        // Excel.Workbook workbook = excelApp.Workbooks.Open(@"C:\path\to\your\file.xlsx");\n\n        // To create a new workbook\n        Excel.Workbook workbook = excelApp.Workbooks.Add(Type.Missing);\n\n        // Adding a new worksheet\n        Excel.Worksheet newWorksheet = (Excel.Worksheet)workbook.Worksheets.Add(Type.Missing, Type.Missing, Type.Missing, Type.Missing);\n\n        // Set the name of the new worksheet\n        newWorksheet.Name = "NewSheet";\n\n        // Save the workbook (optional)\n        // workbook.SaveAs(@"C:\path\to\your\newfile.xlsx");\n\n        // Release the objects\n        System.Runtime.InteropServices.Marshal.ReleaseComObject(newWorksheet);\n        System.Runtime.InteropServices.Marshal.ReleaseComObject(workbook);\n        System.Runtime.InteropServices.Marshal.ReleaseComObject(excelApp);\n\n        newWorksheet = null;\n        workbook = null;\n        excelApp = null;\n\n        GC.Collect();\n    }\n}