using System;\nusing Microsoft.SharePoint;\n\npublic class ItemEventReceiver : SPItemEventReceiver\n{\n    public override void ItemUpdating(SPItemEventProperties properties)\n    {\n        base.ItemUpdating(properties);\n\n        // Check if the current user is the creator of the item\n        if (properties.ListItem["Author"].ToString() != properties.UserDisplayName)\n        {\n            properties.Status = SPEventReceiverStatus.CancelWithError;\n            properties.ErrorMessage = "You can only edit items that you have created.";\n        }\n    }\n}