using System;\nusing System.IO;\nusing Microsoft.Office.Interop.Word;\nusing Microsoft.Office.Interop.Excel;\nusing Microsoft.Office.Interop.PowerPoint;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string folderPath = @"C:\Your\Folder\Path";\n\n        foreach (var file in Directory.GetFiles(folderPath))\n        {\n            string extension = Path.GetExtension(file).ToLower();\n\n            switch (extension)\n            {\n                case ".doc":\n                case ".docx":\n                    ReadWordAuthor(file);\n                    break;\n                case ".xls":\n                case ".xlsx":\n                    ReadExcelAuthor(file);\n                    break;\n                case ".ppt":\n                case ".pptx":\n                    ReadPowerPointAuthor(file);\n                    break;\n                default:\n                    Console.WriteLine($"Unsupported file type: {file}");\n                    break;\n            }\n        }\n    }\n\n    static void ReadWordAuthor(string filePath)\n    {\n        Application wordApp = new Application();\n        Document doc = null;\n        try\n        {\n            doc = wordApp.Documents.Open(filePath, ReadOnly: true);\n            Console.WriteLine($"Word File: {filePath}, Author: {doc.BuiltInDocumentProperties["Author"].Value}");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error reading Word file: {filePath}, {ex.Message}");\n        }\n        finally\n        {\n            doc?.Close(false);\n            wordApp.Quit();\n        }\n    }\n\n    static void ReadExcelAuthor(string filePath)\n    {\n        Application excelApp = new Application();\n        Workbook workbook = null;\n        try\n        {\n            workbook = excelApp.Workbooks.Open(filePath, ReadOnly: true);\n            Console.WriteLine($"Excel File: {filePath}, Author: {workbook.BuiltInDocumentProperties["Author"].Value}");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error reading Excel file: {filePath}, {ex.Message}");\n        }\n        finally\n        {\n            workbook?.Close(false);\n            excelApp.Quit();\n        }\n    }\n\n    static void ReadPowerPointAuthor(string filePath)\n    {\n        Application pptApp = new Application();\n        Presentation presentation = null;\n        try\n        {\n            presentation = pptApp.Presentations.Open(filePath, ReadOnly: Microsoft.Office.Core.MsoTriState.msoTrue);\n            Console.WriteLine($"PowerPoint File: {filePath}, Author: {presentation.BuiltInDocumentProperties["Author"].Value}");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error reading PowerPoint file: {filePath}, {ex.Message}");\n        }\n        finally\n        {\n            presentation?.Close();\n            pptApp.Quit();\n        }\n    }\n}