using System;\nusing System.Data.OleDb;\n\nclass Program\n{\n    static void Main()\n    {\n        bool isJetOLEDBAvailable = IsJetOLEDBProviderAvailable();\n        Console.WriteLine($"Microsoft.Jet.OLEDB.4.0 is {(isJetOLEDBAvailable ? "available" : "not available")} on this machine.");\n    }\n\n    static bool IsJetOLEDBProviderAvailable()\n    {\n        DataTable oleDbProvidersTable = OleDbEnumerator.GetRootEnumerator().GetOleDbSchemaTable(OleDbSchemaGuid.Providers, null);\n\n        foreach (DataRow row in oleDbProvidersTable.Rows)\n        {\n            string providerName = row["SOURCES_NAME"].ToString();\n            if (providerName.Equals("Microsoft.Jet.OLEDB.4.0", StringComparison.OrdinalIgnoreCase))\n            {\n                return true;\n            }\n        }\n\n        return false;\n    }\n}