using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        // Replace "C:" with the desired drive letter\n        string driveLetter = @"C:\";\n\n        DriveInfo driveInfo = new DriveInfo(driveLetter);\n\n        if (driveInfo.IsReady)\n        {\n            switch (driveInfo.DriveType)\n            {\n                case DriveType.Fixed:\n                    Console.WriteLine($"{driveLetter} is a Hard Drive.");\n                    break;\n                case DriveType.Network:\n                    Console.WriteLine($"{driveLetter} is a Network Drive.");\n                    break;\n                case DriveType.CDRom:\n                    Console.WriteLine($"{driveLetter} is a CDRom.");\n                    break;\n                case DriveType.Removable:\n                    Console.WriteLine($"{driveLetter} is a Floppy.");\n                    break;\n                default:\n                    Console.WriteLine($"{driveLetter} is of unknown type.");\n                    break;\n            }\n        }\n        else\n        {\n            Console.WriteLine($"{driveLetter} is not ready.");\n        }\n    }\n}