using System;\nusing System.Net;\nusing System.Net.Http;\nusing System.Net.Http.Headers;\nusing System.Threading.Tasks;\n\npublic class Program\n{\n    public static async Task Main(string[] args)\n    {\n        // Create a handler to manage cookies\n        var handler = new HttpClientHandler();\n        handler.CookieContainer = new CookieContainer();\n\n        // Assuming you have already logged in and have the session cookies\n        // You need to add the cookies to the handler's CookieContainer\n        var baseAddress = new Uri("http://localhost/");\n        handler.CookieContainer.Add(baseAddress, new Cookie("SessionId", "your-session-id"));\n\n        using (var httpClient = new HttpClient(handler))\n        {\n            // Set base address\n            httpClient.BaseAddress = baseAddress;\n\n            // Send a request to the target URL\n            var response = await httpClient.GetAsync("myxml.aspx");\n\n            if (response.IsSuccessStatusCode)\n            {\n                // Read the response content as string\n                var content = await response.Content.ReadAsStringAsync();\n                Console.WriteLine(content);\n\n                // If you need to parse the XML, you can use XmlReader or XDocument\n                // For example, reading with XmlReader\n                using (var reader = new StringReader(content))\n                using (var xmlReader = XmlReader.Create(reader))\n                {\n                    while (xmlReader.Read())\n                    {\n                        // Process the XML data here\n                        Console.WriteLine(xmlReader.ReadOuterXml());\n                    }\n                }\n            }\n            else\n            {\n                Console.WriteLine("Failed to fetch the XML. Status code: " + response.StatusCode);\n            }\n        }\n    }\n}