using System;\nusing System.IO;\nusing System.IO.Compression;\nusing System.Web;\nusing System.Web.Mvc;\n\npublic class ZipController : Controller\n{\n    public ActionResult DownloadZip()\n    {\n        using (MemoryStream zipStream = new MemoryStream())\n        {\n            using (ZipArchive zip = new ZipArchive(zipStream, ZipArchiveMode.Create, true))\n            {\n                // Add files to the zip archive\n                ZipArchiveEntry entry = zip.CreateEntry("example.txt");\n                using (StreamWriter writer = new StreamWriter(entry.Open()))\n                {\n                    writer.Write("This is an example text file.");\n                }\n\n                // Add more files as needed\n            }\n\n            // Set the position of the MemoryStream to the beginning\n            zipStream.Position = 0;\n\n            // Return the zip file as a downloadable response\n            return File(zipStream.ToArray(), "application/zip", "download.zip");\n        }\n    }\n}