public class Person\n{\n    public string Name { get; set; }\n    public int Age { get; set; }\n    public List<string> Skills { get; set; }\n}\n\npublic class Program\n{\n    public static void Main(string[] args)\n    {\n        var yaml = @"\n        name: Example\n        age: 30\n        skills:\n          - C#\n          - .NET\n          - YAML\n        ";\n\n        // Set up the deserializer\n        var deserializer = new DeserializerBuilder()\n            .WithNamingConvention(CamelCaseNamingConvention.Instance) // Use camel case naming convention\n            .Build();\n\n        // Deserialize the YAML string to a strongly-typed object\n        var person = deserializer.Deserialize<Person>(yaml);\n\n        // Access the data\n        Console.WriteLine(person.Name); // Output: Example\n        Console.WriteLine(person.Age);  // Output: 30\n        Console.WriteLine(person.Skills[0]); // Output: C#\n    }\n}