public class GridPresenter\n{\n    private readonly IGridView _view;\n    private readonly IGridModel _model;\n\n    public GridPresenter(IGridView view, IGridModel model)\n    {\n        _view = view;\n        _model = model;\n    }\n\n    public void UpdateCellStyleAndValue(int row, int column)\n    {\n        // Determine the style and value based on some logic\n        var newValue = _model.GetCellValue(row, column);\n        var newStyle = _model.GetCellStyle(row, column);\n\n        // Update the view\n        _view.SetCellValue(row, column, newValue);\n        _view.SetCellStyle(row, column, newStyle);\n    }\n}