using System;\nusing Azure.Identity;\nusing Azure.Security.KeyVault.Keys;\nusing Azure.Security.KeyVault.Keys.Cryptography;\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        var keyVaultName = "your-key-vault-name";\n        var keyVaultUri = $"https://{keyVaultName}.vault.azure.net/";\n\n        var client = new KeyClient(new Uri(keyVaultUri), new DefaultAzureCredential());\n\n        // Create a new key\n        string keyName = "myKey";\n        KeyVaultKey key = await client.CreateKeyAsync(keyName, KeyType.Rsa);\n\n        // Retrieve the key\n        KeyVaultKey retrievedKey = await client.GetKeyAsync(keyName);\n\n        Console.WriteLine($"Key ID: {retrievedKey.Id}");\n    }\n}