using System;\nusing System.Data;\nusing Oracle.ManagedDataAccess.Client;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "Your Oracle Connection String";\n        using (OracleConnection conn = new OracleConnection(connectionString))\n        {\n            conn.Open();\n\n            Guid guid = Guid.NewGuid();\n            byte[] guidBytes = guid.ToByteArray();\n\n            using (OracleCommand cmd = new OracleCommand("INSERT INTO your_table (id, other_column) VALUES (:id, :other_column)", conn))\n            {\n                cmd.Parameters.Add(":id", OracleDbType.Raw, guidBytes, ParameterDirection.Input);\n                cmd.Parameters.Add(":other_column", OracleDbType.Varchar2, "Some Value", ParameterDirection.Input);\n\n                cmd.ExecuteNonQuery();\n            }\n        }\n    }\n}