using System;\nusing System.Web;\n\npublic class SessionEventsModule : IHttpModule\n{\n    public void Init(HttpApplication context)\n    {\n        context.PostAcquireRequestState += new EventHandler(this.Application_PostAcquireRequestState);\n    }\n\n    private void Application_PostAcquireRequestState(object sender, EventArgs e)\n    {\n        HttpApplication application = (HttpApplication)sender;\n        HttpContext context = application.Context;\n\n        if (context.Session != null)\n        {\n            context.Session.Start += new EventHandler(this.Session_Start);\n            context.Session.End += new EventHandler(this.Session_End);\n        }\n    }\n\n    private void Session_Start(object sender, EventArgs e)\n    {\n        // Your logic for Session_Start\n    }\n\n    private void Session_End(object sender, EventArgs e)\n    {\n        // Your logic for Session_End\n    }\n\n    public void Dispose()\n    {\n        // Clean-up code here.\n    }\n}