using System;\nusing System.Text.RegularExpressions;\nusing System.Xml;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string xmlContent = "<Element>&#x1F;&#x1C;Some Text</Element>";\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.LoadXml(xmlContent);\n\n        string sanitizedXml = SanitizeXmlString(xmlDoc.OuterXml);\n        Console.WriteLine(sanitizedXml);\n        // Now you can safely insert sanitizedXml into your SQL database\n    }\n\n    public static string SanitizeXmlString(string xml)\n    {\n        // Remove illegal XML characters\n        string pattern = @"[^\x09\x0A\x0D\x20-\xD7FF\xE000-\xFFFD\x10000-\x10FFFF]";\n        return Regex.Replace(xml, pattern, "");\n    }\n}