using Gtk;\nusing System;\n\npublic class ContextMenuExample : Window\n{\n    private TextView textView;\n    private Menu contextMenu;\n\n    public ContextMenuExample() : base("Context Menu Example")\n    {\n        SetDefaultSize(400, 200);\n        SetPosition(WindowPosition.Center);\n        \n        textView = new TextView();\n        textView.ButtonPressEvent += OnTextViewButtonPressEvent;\n\n        contextMenu = new Menu();\n        MenuItem copyItem = new MenuItem("Copy");\n        MenuItem pasteItem = new MenuItem("Paste");\n        contextMenu.Append(copyItem);\n        contextMenu.Append(pasteItem);\n        contextMenu.ShowAll();\n\n        Add(textView);\n        ShowAll();\n    }\n\n    private void OnTextViewButtonPressEvent(object sender, ButtonPressEventArgs args)\n    {\n        if (args.Event.Button == 3) // Right-click\n        {\n            contextMenu.Popup();\n        }\n    }\n\n    public static void Main()\n    {\n        Application.Init();\n        new ContextMenuExample();\n        Application.Run();\n    }\n}