using System;\nusing System.Web.Security;\n\npublic partial class Admin_ManageUsers : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        // Display the list of users\n        var users = Membership.GetAllUsers();\n        UsersGridView.DataSource = users;\n        UsersGridView.DataBind();\n    }\n\n    protected void CreateUserButton_Click(object sender, EventArgs e)\n    {\n        string username = UsernameTextBox.Text;\n        string password = PasswordTextBox.Text;\n        string email = EmailTextBox.Text;\n\n        MembershipCreateStatus status;\n        Membership.CreateUser(username, password, email, null, null, true, out status);\n\n        if (status == MembershipCreateStatus.Success)\n        {\n            // User created successfully\n        }\n        else\n        {\n            // Handle errors\n        }\n    }\n\n    protected void DeleteUserButton_Click(object sender, EventArgs e)\n    {\n        string username = UsernameToDeleteTextBox.Text;\n        Membership.DeleteUser(username);\n    }\n\n    protected void CreateRoleButton_Click(object sender, EventArgs e)\n    {\n        string roleName = RoleNameTextBox.Text;\n        if (!Roles.RoleExists(roleName))\n        {\n            Roles.CreateRole(roleName);\n        }\n    }\n\n    protected void AssignRoleButton_Click(object sender, EventArgs e)\n    {\n        string username = UsernameForRoleTextBox.Text;\n        string roleName = RoleNameForAssignmentTextBox.Text;\n\n        if (Roles.RoleExists(roleName))\n        {\n            Roles.AddUserToRole(username, roleName);\n        }\n    }\n}