using System;\nusing System.Drawing;\nusing System.Windows.Forms;\n\npublic class RichTextBoxHighlighter : Form\n{\n    private RichTextBox richTextBox;\n\n    public RichTextBoxHighlighter()\n    {\n        richTextBox = new RichTextBox();\n        richTextBox.Dock = DockStyle.Fill;\n        this.Controls.Add(richTextBox);\n        this.Load += new EventHandler(Form_Load);\n    }\n\n    private void Form_Load(object sender, EventArgs e)\n    {\n        richTextBox.Text = "This is a sample text with several A letters. A A A";\n        HighlightLetterA();\n    }\n\n    private void HighlightLetterA()\n    {\n        string text = richTextBox.Text;\n        int startIndex = 0;\n\n        while ((startIndex = text.IndexOf('A', startIndex)) != -1)\n        {\n            richTextBox.Select(startIndex, 1);\n            richTextBox.SelectionColor = Color.Red;\n            startIndex++;\n        }\n\n        // Reset the selection to the start\n        richTextBox.Select(0, 0);\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new RichTextBoxHighlighter());\n    }\n}