using System;\nusing System.Net;\nusing YourNamespace.ReportingService2010; // Namespace from your service reference\n\npublic class SSRSSubscriptionHelper\n{\n    public void CreateReportSubscription()\n    {\n        ReportingService2010 rs = new ReportingService2010();\n        rs.Credentials = CredentialCache.DefaultNetworkCredentials;\n\n        string reportPath = "/YourReportPath/ReportName";\n        string subscriptionID;\n\n        ExtensionSettings extSettings = new ExtensionSettings();\n        extSettings.Extension = "Report Server Email";\n\n        ParameterValue[] extensionParams = new ParameterValue[3];\n        extensionParams[0] = new ParameterValue();\n        extensionParams[0].Name = "TO";\n        extensionParams[0].Value = "user@example.com";\n        extensionParams[1] = new ParameterValue();\n        extensionParams[1].Name = "IncludeReport";\n        extensionParams[1].Value = "True";\n        extensionParams[2] = new ParameterValue();\n        extensionParams[2].Name = "RenderFormat";\n        extensionParams[2].Value = "PDF";\n\n        extSettings.ParameterValues = extensionParams;\n\n        ScheduleDefinition schedule = new ScheduleDefinition();\n        schedule.StartDateTime = DateTime.Now;\n        DailyRecurrence recurrence = new DailyRecurrence();\n        recurrence.DaysInterval = 1;\n        schedule.Item = recurrence;\n\n        ParameterValue[] parameters = null;\n\n        try\n        {\n            subscriptionID = rs.CreateSubscription(reportPath, extSettings, "Sample Subscription", null, schedule, parameters);\n            Console.WriteLine("Subscription created successfully with ID: " + subscriptionID);\n        }\n        catch (Exception e)\n        {\n            Console.WriteLine("Error creating subscription: " + e.Message);\n        }\n    }\n}