public bool ValidateToken(string token, string secretKey)\n     {\n         var parts = token.Split(':');\n         if (parts.Length != 2) return false;\n\n         var hmac = new System.Security.Cryptography.HMACSHA256(System.Text.Encoding.UTF8.GetBytes(secretKey));\n         var expectedSignature = Convert.ToBase64String(hmac.ComputeHash(System.Text.Encoding.UTF8.GetBytes(parts[0])));\n\n         return expectedSignature == parts[1];\n     }