using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    static void Main()\n    {\n        Type excelType = Type.GetTypeFromProgID("Excel.Application.9"); // Excel 9 corresponds to Excel 2000\n        if (excelType == null)\n        {\n            Console.WriteLine("Excel 9 is not installed on this machine.");\n            return;\n        }\n\n        object excelApp = Activator.CreateInstance(excelType);\n        if (excelApp == null)\n        {\n            Console.WriteLine("Could not create an instance of Excel 9.");\n            return;\n        }\n\n        // Make Excel visible (optional)\n        excelType.InvokeMember("Visible", System.Reflection.BindingFlags.SetProperty, null, excelApp, new object[] { true });\n\n        // Your automation code here...\n        // Example: Open a workbook\n        object workbooks = excelType.InvokeMember("Workbooks", System.Reflection.BindingFlags.GetProperty, null, excelApp, null);\n        excelType.InvokeMember("Open", System.Reflection.BindingFlags.InvokeMethod, null, workbooks, new object[] { "C:\\path\\to\\your\\spreadsheet.xls" });\n\n        // Cleanup\n        Marshal.ReleaseComObject(workbooks);\n        Marshal.ReleaseComObject(excelApp);\n    }\n}