using System;\nusing System.Data.OleDb;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string strTest = "Hash.System.Data.DataSet has.ds";\n\n        // Define the connection string (update with your database path)\n        string connectionString = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=your-database-path.mdb;";\n\n        using (OleDbConnection connection = new OleDbConnection(connectionString))\n        {\n            connection.Open();\n\n            // Define the query to select all ItemIdentity values\n            string query = "SELECT ItemIdentity FROM HashTableConfig";\n            OleDbCommand command = new OleDbCommand(query, connection);\n\n            using (OleDbDataReader reader = command.ExecuteReader())\n            {\n                while (reader.Read())\n                {\n                    string itemIdentity = reader["ItemIdentity"].ToString();\n\n                    // Remove parts enclosed in parentheses\n                    string modifiedStrTest = Regex.Replace(strTest, @"\s*\(.*?\)\s*", "");\n                    string modifiedItemIdentity = Regex.Replace(itemIdentity, @"\s*\(.*?\)\s*", "");\n\n                    // Compare the modified strings\n                    if (modifiedStrTest == modifiedItemIdentity)\n                    {\n                        Console.WriteLine($"Match found: {itemIdentity}");\n                    }\n                }\n            }\n\n            connection.Close();\n        }\n    }\n}