using System;\nusing System.IO;\n\npublic class PathChecker\n{\n    public static bool IsUncPath(string path)\n    {\n        if (string.IsNullOrWhiteSpace(path))\n        {\n            throw new ArgumentException("Path cannot be null or empty.", nameof(path));\n        }\n\n        Uri uri;\n        if (Uri.TryCreate(path, UriKind.Absolute, out uri))\n        {\n            return uri.IsUnc;\n        }\n\n        // If the path is relative, it's not a UNC path\n        return false;\n    }\n\n    public static DriveInfo GetDriveInfo(string path)\n    {\n        if (!IsUncPath(path))\n        {\n            // Check if the path starts with a drive letter\n            if (path.Length > 1 && path[1] == ':' && Char.IsLetter(path[0]))\n            {\n                string driveLetter = path.Substring(0, 2);\n                return new DriveInfo(driveLetter);\n            }\n        }\n\n        throw new ArgumentException("The path does not contain a valid drive letter.", nameof(path));\n    }\n}