using System;\nusing System.Collections.Generic;\n\nclass LoremIpsum {\n    static void Main() {\n        List<string> words = new List<string> { "Lorem", "ipsum", "dolor", "sit", "amet", "consectetur", "adipiscing", "elit" };\n        \n        foreach (string word in words) {\n            int length = LoremIpsumLength(word);\n            Console.WriteLine($"Length of '{word}': {length}");\n        }\n    }\n\n    static int LoremIpsumLength(string text) {\n        Console.WriteLine($"Processing text: {text}");\n        return text.Length;\n    }\n}