using System;\nusing System.Data;\nusing System.Linq;\n\npublic class HomePageNews\n{\n    public void BuildHomePageSections()\n    {\n        HHNews hhNews = new HHNews();\n        DataTable dt = hhNews.GetNewsDataTable();\n\n        // Filtering top 5 headlines (NewsType = 0)\n        var headlines = (from row in dt.AsEnumerable()\n                         where row.Field<int>("NewsType") == 0\n                         orderby row.Field<DateTime>("DateAdded") descending\n                         select row).Take(5);\n\n        // Displaying Headlines\n        Console.WriteLine("Headlines:");\n        foreach (var row in headlines)\n        {\n            Console.WriteLine($"NewsID: {row.Field<int>("NewsID")}, NewsTitle: {row.Field<string>("NewsTitle")}, NewsText: {row.Field<string>("NewsText")}");\n        }\n\n        // Similarly, you can query for Sub-headings, and Last 5 news items for each of the news categories\n        // Example for Sub-headings (assuming NewsType = 1 for Sub-headings)\n        var subHeadings = (from row in dt.AsEnumerable()\n                           where row.Field<int>("NewsType") == 1\n                           orderby row.Field<DateTime>("DateAdded") descending\n                           select row).Take(4);\n\n        // Displaying Sub-headings\n        Console.WriteLine("\nSub-headings:");\n        foreach (var row in subHeadings)\n        {\n            Console.WriteLine($"NewsID: {row.Field<int>("NewsID")}, NewsTitle: {row.Field<string>("NewsTitle")}, NewsText: {row.Field<string>("NewsText")}");\n        }\n\n        // Example for Last 5 news items for each category\n        var categories = dt.AsEnumerable().Select(row => row.Field<int>("CategoryID")).Distinct();\n        foreach (var category in categories)\n        {\n            var categoryNews = (from row in dt.AsEnumerable()\n                                where row.Field<int>("CategoryID") == category\n                                orderby row.Field<DateTime>("DateAdded") descending\n                                select row).Take(5);\n\n            Console.WriteLine($"\nCategory {category} News:");\n            foreach (var row in categoryNews)\n            {\n                Console.WriteLine($"NewsID: {row.Field<int>("NewsID")}, NewsTitle: {row.Field<string>("NewsTitle")}, NewsText: {row.Field<string>("NewsText")}");\n            }\n        }\n    }\n}\n\n// Assuming HHNews class implementation\npublic class HHNews\n{\n    public DataTable GetNewsDataTable()\n    {\n        // Implementation to get the DataTable\n        // For example, you might be filling it from a database\n        return new DataTable();\n    }\n}