public string GenerateToken(string userId, string secretKey)\n     {\n         var payload = $"{userId}:{DateTime.UtcNow.Ticks}";\n         var token = Convert.ToBase64String(System.Text.Encoding.UTF8.GetBytes(payload));\n         var hmac = new System.Security.Cryptography.HMACSHA256(System.Text.Encoding.UTF8.GetBytes(secretKey));\n         var signature = Convert.ToBase64String(hmac.ComputeHash(System.Text.Encoding.UTF8.GetBytes(token)));\n         return $"{token}:{signature}";\n     }