using System;\nusing Perforce.P4;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Define the Perforce server connection details\n        string serverAddress = "perforce:1666";\n        string userName = "your_username";\n        string password = "your_password";\n        string workspace = "your_workspace";\n\n        // Create a connection to the Perforce server\n        Server server = new Server(new ServerAddress(serverAddress));\n        Repository repository = new Repository(server);\n        Connection connection = repository.Connection;\n\n        // Connect to the server\n        connection.UserName = userName;\n        connection.Client = new Client { Name = workspace };\n\n        bool connected = connection.Connect(null);\n        \n        if (connected)\n        {\n            Console.WriteLine("Connected to Perforce server.");\n\n            // Login to the server\n            connection.Login(password);\n\n            // Example: Get the changelist descriptions\n            Options options = new Options(ChangeListCmdFlags.None, -1);\n            IList<Changelist> changelists = repository.GetChangelists(options);\n\n            foreach (Changelist changelist in changelists)\n            {\n                Console.WriteLine($"Changelist ID: {changelist.Id}, Description: {changelist.Description}");\n            }\n\n            // Disconnect from the server\n            connection.Disconnect();\n        }\n        else\n        {\n            Console.WriteLine("Failed to connect to Perforce server.");\n        }\n    }\n}