using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string xmlContent = @"<root>\n                                <person_data>\n                                    <person>\n                                        <name>John</name>\n                                        <age>35</age>\n                                    </person>\n                                    <person>\n                                        <name>Jim</name>\n                                        <age>50</age>\n                                    </person>\n                                </person_data>\n                                <locations>\n                                    <location>\n                                        <name>John</name>\n                                        <country>USA</country>\n                                    </location>\n                                    <location>\n                                        <name>Jim</name>\n                                        <country>Japan</country>\n                                    </location>\n                                </locations>\n                              </root>";\n\n        XmlDocument doc = new XmlDocument();\n        doc.LoadXml(xmlContent);\n\n        // Select the person node for Jim\n        XmlNode personNode = doc.SelectSingleNode("//person[name='Jim']");\n        if (personNode != null)\n        {\n            // Retrieve the name value from the personNode\n            string personName = personNode.SelectSingleNode("name").InnerText;\n\n            // Construct XPath for location node based on the name value\n            string xpath = $"//location[name='{personName}']";\n            XmlNode locationNode = doc.SelectSingleNode(xpath);\n\n            if (locationNode != null)\n            {\n                Console.WriteLine("Location found:");\n                Console.WriteLine(locationNode.OuterXml);\n            }\n            else\n            {\n                Console.WriteLine("Location not found.");\n            }\n        }\n        else\n        {\n            Console.WriteLine("Person not found.");\n        }\n    }\n}