protected void Application_Error(object sender, EventArgs e)\n{\n    Exception exception = Server.GetLastError();\n    HttpException httpException = exception as HttpException;\n\n    if (httpException != null && httpException.GetHttpCode() == 404)\n    {\n        // Log the details of the 404 error\n        string requestedUrl = Request.Url.ToString();\n        string referrerUrl = Request.UrlReferrer != null ? Request.UrlReferrer.ToString() : "No referrer";\n        string userIpAddress = Request.UserHostAddress;\n\n        // Use your preferred logging mechanism\n        // For example: Log4Net, NLog, Serilog, etc.\n        Log.Error($"404 Error: Requested URL: {requestedUrl}, Referrer: {referrerUrl}, IP Address: {userIpAddress}");\n    }\n}