using System;\nusing System.DirectoryServices;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            // Connect to the IIS metabase\n            DirectoryEntry iis = new DirectoryEntry("IIS://localhost");\n\n            // Invoke the Backup method\n            object[] parameters = new object[] { "MyBackup", 0 };\n            iis.Invoke("Backup", parameters);\n\n            Console.WriteLine("Backup completed successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n    }\n}