using System;\n   using System.Data.SqlClient;\n   using TikaOnDotNet.TextExtraction;\n\n   public class DocumentIndexer\n   {\n       private string connectionString = "YourConnectionStringHere";\n\n       public void IndexDocument(byte[] fileBytes, string fileName)\n       {\n           var textExtractor = new TextExtractor();\n           string extractedText = textExtractor.Extract(fileBytes, fileName).Text;\n\n           // Save extracted text to the database\n           SaveExtractedTextToDatabase(fileName, extractedText);\n       }\n\n       private void SaveExtractedTextToDatabase(string fileName, string extractedText)\n       {\n           using (SqlConnection connection = new SqlConnection(connectionString))\n           {\n               string query = "INSERT INTO DocumentIndex (FileName, ExtractedText) VALUES (@FileName, @ExtractedText)";\n               using (SqlCommand command = new SqlCommand(query, connection))\n               {\n                   command.Parameters.AddWithValue("@FileName", fileName);\n                   command.Parameters.AddWithValue("@ExtractedText", extractedText);\n\n                   connection.Open();\n                   command.ExecuteNonQuery();\n               }\n           }\n       }\n   }