using System;\nusing unoidl.com.sun.star.bridge;\nusing unoidl.com.sun.star.comp.helper;\nusing unoidl.com.sun.star.lang;\nusing unoidl.com.sun.star.uno;\nusing unoidl.com.sun.star.sheet;\nusing unoidl.com.sun.star.text;\n\nnamespace OpenOfficeMailMerge\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // Bootstrap OpenOffice\n            XComponentContext xLocalContext = uno.util.Bootstrap.bootstrap();\n            XMultiServiceFactory xMSF = (XMultiServiceFactory)xLocalContext.getServiceManager();\n            XTextDocument xTextDocument = null;\n\n            try\n            {\n                // Load the document\n                XComponentLoader xComponentLoader = (XComponentLoader)xMSF.createInstance("com.sun.star.frame.Desktop");\n                XComponent xComponent = xComponentLoader.loadComponentFromURL("file:///path/to/your/document.odt", "_blank", 0, new unoidl.com.sun.star.beans.PropertyValue[0]);\n                xTextDocument = (XTextDocument)xComponent;\n\n                // Access the mail merge service\n                XMultiServiceFactory xDocMSF = (XMultiServiceFactory)xTextDocument;\n                XMailMerge xMailMerge = (XMailMerge)xDocMSF.createInstance("com.sun.star.text.MailMerge");\n\n                // Set up mail merge properties\n                xMailMerge.DataSourceName = "yourDataSource";\n                xMailMerge.CommandType = CommandType.TABLE;\n                xMailMerge.Command = "yourTableName";\n                xMailMerge.OutputType = MailMergeType.FILE;\n                xMailMerge.OutputURL = "file:///path/to/output/";\n\n                // Execute the mail merge\n                xMailMerge.execute(new unoidl.com.sun.star.beans.PropertyValue[0]);\n\n                Console.WriteLine("Mail merge completed successfully.");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("An error occurred: " + ex.Message);\n            }\n            finally\n            {\n                if (xTextDocument != null)\n                {\n                    xTextDocument.dispose();\n                }\n            }\n        }\n    }\n}