public class Product\n{\n    // Backing field for integer price\n    private int _price;\n\n    // Property to get/set price as decimal\n    public decimal Price\n    {\n        get\n        {\n            return _price / 100m; // Convert to decimal by dividing by 100\n        }\n        set\n        {\n            _price = (int)(value * 100); // Convert decimal to integer by multiplying by 100\n        }\n    }\n    \n    // Constructor\n    public Product(int price)\n    {\n        _price = price;\n    }\n}