cmd.CommandText = "EXEC [dbo].insert_test_result @executed, @failure, @success, @testID, @time, @name, @description, @executionDateTime, @message;";\n\ncmd.Parameters.Add("@executed", SqlDbType.Bit).Value = result.Executed;\ncmd.Parameters.Add("@failure", SqlDbType.Bit).Value = result.Failure;\ncmd.Parameters.Add("@success", SqlDbType.Bit).Value = result.Success;\ncmd.Parameters.Add("@testID", SqlDbType.Int).Value = result.TestID;\ncmd.Parameters.Add("@time", SqlDbType.Float).Value = result.Time;\n\ncmd.Parameters.Add("@name", SqlDbType.VarChar, 200).Value = result.Name ?? (object)DBNull.Value;\ncmd.Parameters.Add("@description", SqlDbType.VarChar, 200).Value = result.Description ?? (object)DBNull.Value;\ncmd.Parameters.Add("@executionDateTime", SqlDbType.NVarChar, -1).Value = result.ExecutionDateTime ?? (object)DBNull.Value;\ncmd.Parameters.Add("@message", SqlDbType.NVarChar, -1).Value = result.Message ?? (object)DBNull.Value;\n\ntry\n{\n    connection.Open();\n    cmd.ExecuteNonQuery();\n}\nfinally\n{\n    connection.Close();\n}