using System;\nusing System.IO;\n\npublic class DirectoryCopy\n{\n    public static void CopyDirectory(string sourceDir, string destDir, bool copySubDirs)\n    {\n        DirectoryInfo dir = new DirectoryInfo(sourceDir);\n\n        if (!dir.Exists)\n        {\n            throw new DirectoryNotFoundException(\n                "Source directory does not exist or could not be found: "\n                + sourceDir);\n        }\n\n        DirectoryInfo[] dirs = dir.GetDirectories();\n        Directory.CreateDirectory(destDir);\n\n        FileInfo[] files = dir.GetFiles();\n        foreach (FileInfo file in files)\n        {\n            string tempPath = Path.Combine(destDir, file.Name);\n            file.CopyTo(tempPath, false);\n        }\n\n        if (copySubDirs)\n        {\n            foreach (DirectoryInfo subdir in dirs)\n            {\n                string tempPath = Path.Combine(destDir, subdir.Name);\n                CopyDirectory(subdir.FullName, tempPath, true);\n            }\n        }\n    }\n\n    public static void Main()\n    {\n        string sourceDirectory = @"C:\SourceDirectory";\n        string destinationDirectory = @"C:\DestinationDirectory";\n\n        try\n        {\n            CopyDirectory(sourceDirectory, destinationDirectory, true);\n            Console.WriteLine("Directory copied successfully!");\n        }\n        catch (Exception e)\n        {\n            Console.WriteLine("An error occurred: " + e.Message);\n        }\n    }\n}