public class GridView : IGridView\n{\n    public void SetCellStyle(int row, int column, CellStyle style)\n    {\n        // Update the cell style in the grid\n        grid.SetCellStyle(row, column, style);\n    }\n\n    public void SetCellValue(int row, int column, object value)\n    {\n        // Update the cell value in the grid\n        grid.SetCellValue(row, column, value);\n    }\n}