public class GridPresenter\n{\n    private readonly IGridView _view;\n    private readonly IGridModel _model;\n\n    public GridPresenter(IGridView view, IGridModel model)\n    {\n        _view = view;\n        _model = model;\n        _view.CellUpdated += OnCellUpdated;\n    }\n\n    private void OnCellUpdated(object sender, CellUpdatedEventArgs e)\n    {\n        // Perform validation, update the model, etc.\n        _model.UpdateCell(e.Row, e.Column, e.NewValue);\n    }\n}