public class GridView : IGridView\n{\n    public event EventHandler<CellUpdatedEventArgs> CellUpdated;\n\n    private void OnCellUpdated(int row, int column, object newValue)\n    {\n        CellUpdated?.Invoke(this, new CellUpdatedEventArgs(row, column, newValue));\n    }\n    \n    // Method to be called by the grid when a cell update occurs\n    private void Grid_CellUpdated(object sender, GridCellEventArgs e)\n    {\n        OnCellUpdated(e.Row, e.Column, e.NewValue);\n    }\n}