public class ModuleMap : ClassMap<Module>\n{\n    public ModuleMap()\n    {\n        Id(x => x.ModuleId);\n        Map(x => x.LastUpdated);\n        References(x => x.StratumVariable);\n        References(x => x.DefaultStratum);\n        HasMany(x => x.Variables)\n            .Cascade.All()\n            .Fetch.Select(); // Specify fetch strategy\n        HasMany(x => x.OptionalStrata)\n            .Cascade.All()\n            .Fetch.Select(); // Specify fetch strategy\n    }\n}