var query = from a in context.Appointments\n            where a.RowStatus == 1 && a.Type == 1\n            join d in context.Types on a.Type equals d.TypeId\n            join b in context.AppointmentFormulas.Where(b => b.RowStatus == 1) on a.AppointmentId equals b.AppointmentId into bGroup\n            from b in bGroup.DefaultIfEmpty()\n            join e in context.AppointmentForms on a.AppointmentId equals e.AppointmentId into eGroup\n            from e in eGroup.DefaultIfEmpty()\n            orderby a.Type\n            select new\n            {\n                a.AppointmentId,\n                a.Status,\n                a.Type,\n                a.Title,\n                Days = b != null ? b.Days : (int?)null,\n                d.Description,\n                FormId = e != null ? e.FormId : (int?)null\n            };\n\nvar result = query.ToList();