using (ISession session = sessionFactory.OpenSession())\n{\n    var entity = session.Get<Entity>(1); // Load entity with EntityId = 1\n    \n    foreach (var varcharValue in entity.VarcharValues)\n    {\n        Console.WriteLine($"Varchar Value: {varcharValue.VarcharValue}");\n    }\n\n    foreach (var floatValue in entity.FloatValues)\n    {\n        Console.WriteLine($"Float Value: {floatValue.FloatValue}");\n    }\n}