try\n{\n    // Assume 'dataSet' is your DataSet and you're trying to fill it\n    dataAdapter.Fill(dataSet);\n}\ncatch (ConstraintException)\n{\n    foreach (DataTable table in dataSet.Tables)\n    {\n        DataRow[] errorRows = table.GetErrors();\n        foreach (DataRow row in errorRows)\n        {\n            foreach (DataColumn column in table.Columns)\n            {\n                if (row.GetColumnError(column) != string.Empty)\n                {\n                    Console.WriteLine($"Error in column {column.ColumnName}, Value: {row[column]}");\n                }\n            }\n        }\n    }\n}