using System;\nusing System.Security.Cryptography;\n\nclass Program\n{\n    static void Main()\n    {\n        // Generate a new RSA key pair\n        using (RSACryptoServiceProvider rsa = new RSACryptoServiceProvider())\n        {\n            // Export the private key\n            byte[] privateKey = rsa.ExportCspBlob(true);\n\n            // Export the public key\n            byte[] publicKey = rsa.ExportCspBlob(false);\n\n            // Display the keys\n            Console.WriteLine("Private Key: " + Convert.ToBase64String(privateKey));\n            Console.WriteLine("Public Key: " + Convert.ToBase64String(publicKey));\n        }\n    }\n}