<%@ WebHandler Language="C#" Class="ImageHandler" %>\n\nusing System;\nusing System.Drawing;\nusing System.Drawing.Imaging;\nusing System.Web;\n\npublic class ImageHandler : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        // Set the content type to image/jpeg\n        context.Response.ContentType = "image/jpeg";\n\n        // Create a bitmap\n        using (Bitmap bitmap = new Bitmap(200, 100))\n        {\n            using (Graphics g = Graphics.FromImage(bitmap))\n            {\n                // Draw a simple chart (for example, a bar)\n                g.Clear(Color.White);\n                g.FillRectangle(Brushes.Blue, 10, 10, 180, 80);\n                g.DrawString("Sample Chart", new Font("Arial", 16), Brushes.Black, new PointF(30, 40));\n\n                // Save the image to the Response stream\n                bitmap.Save(context.Response.OutputStream, ImageFormat.Jpeg);\n            }\n        }\n    }\n\n    public bool IsReusable\n    {\n        get { return false; }\n    }\n}