using System;\nusing YamlDotNet.Serialization;\nusing YamlDotNet.Serialization.NamingConventions;\n\npublic class Program\n{\n    public static void Main(string[] args)\n    {\n        var yaml = @"\n        name: Example\n        age: 30\n        skills:\n          - C#\n          - .NET\n          - YAML\n        ";\n\n        // Set up the deserializer\n        var deserializer = new DeserializerBuilder()\n            .WithNamingConvention(CamelCaseNamingConvention.Instance) // Use camel case naming convention\n            .Build();\n\n        // Deserialize the YAML string to a dynamic object\n        var person = deserializer.Deserialize<dynamic>(yaml);\n\n        // Access the data\n        Console.WriteLine(person["name"]); // Output: Example\n        Console.WriteLine(person["age"]);  // Output: 30\n        Console.WriteLine(person["skills"][0]); // Output: C#\n    }\n}